'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/float64/eps' );
var abs = require( '@stdlib/math/base/special/abs' );
var acsch = require( '@stdlib/math/base/special/acsch/lib' );


// FIXTURES //

var tinyNegative = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/tiny_negative' );
var tinyPositive = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/tiny_positive' );
var mediumNegative = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/medium_negative' );
var mediumPositive = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/medium_positive' );
var largeNegative = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/large_negative' );
var largePositive = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/large_positive' );
var hugeNegative = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/huge_negative' );
var hugePositive = require( '@stdlib/math/base/special/acsch/test/fixtures/julia/huge_positive' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.true( typeof acsch, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function computes the hyperbolic arccosine on the interval [-1.0, -0.1]', function test( t ) {
	var expected;
	var delta;
	var tol;
	var x;
	var y;
	var i;

	x = tinyNegative.x;
	expected = tinyNegative.expected;

	for ( i = 0; i < x.length; i++ ) {
		y = acsch( x[i] );
		if ( y === expected[ i ] ) {
			t.equal( y, expected[ i ], 'x: '+x[i]+'. E: '+expected[i] );
		} else {
			delta = abs( y - expected[i] );
			tol = 1.5 * EPS * abs( expected[i] );
			t.ok( delta <= tol, 'within tolerance. x: '+x[i]+'. y: '+y+'. E: '+expected[i]+'. tol: '+tol+'. Δ: '+delta+'.' );
		}
	}
	t.end();
});

