tape( 'the function returns `NaN` if provided `NaN`', function test( t ) {
	var v = acsch( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `NaN` if provided value is `0`', function test( t ) {
	let v = 0;
	t.equal( isnan( acsch( v ) ), true, 'returns NaN when provided '+v );
	t.end();
});
