'use strict';

// MODULES //

var sin = require( '@stdlib/math/base/special/sin' );
var deg2rad = require( '@stdlib/math/base/special/deg2rad' );
var isInteger = require( '@stdlib/math/base/assert/is-integer' );
var isInfinite = require( '@stdlib/assert/is-infinite' );


// MAIN //

/**
* Computes the sine of an angle measured in degrees, for a double-precision floating-point number.
*
* @param {number} x - input value (in degrees)
* @returns {number} sine
*
* @example
* var v = sind( 0.0 );
* // returns 0.0
*
* @example
* var v = sind( 30.0 );
* // returns 0.5
*
* @example
* var v = sind( 90.0 );
* // returns 1.0
*
* @example
* var v = sind( NaN );
* // returns NaN
*/
function sind( x ) {
	var xRad;
	if ( isInfinite( x ) ) {
		return NaN;
	}
	if ( isInteger( x / 180.0 ) ) {
		return 0.0;
	}
	xRad = deg2rad( x );
	return sin( xRad );
}


// EXPORTS //

module.exports = sind;
